class CarrerasSede extends BaseCarrerasSede
{

    // Obtener los datos especificos de una carrera y sede
    public function cursandopierderegularidad($idcarrera, $idsede)
    {
    	$q = Doctrine_Query::create()
    	->select('cs.*')
    	->from('CarrerasSede cs')
    	->where('cs.idsede = '.$idsede)
    	->andWhere('cs.idcarrera = '.$idcarrera)
    	->andWhere('cs.cursapierderegular = 1');

    	return $q->fetchOne();

    	//return $q->execute();	
    }
}
