<?php

/**
 * DocumentacionTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class DocumentacionTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object DocumentacionTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Documentacion');
    }
    
    public static function getDocumentacionesPorTipo($tipo=3)
    {
    	return Doctrine_Query::create()
    		->select('*')
    		->from('Documentacion')
    		->where('idtipodocumentacion = ? ', $tipo)
    		->execute();
    }    
}