<?php

/**
 * FechasCalendarioTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class FechasCalendarioTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object FechasCalendarioTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('FechasCalendario');
    }

	// Busca el alumno
  	public function obtenerPeriodosMesasCalendario($idcalendario)
    {
    	$q = Doctrine_Query::create()
	  		->select('fc.descripcion')
	 		->from('FechasCalendario fc')
	 		->where('fc.idcalendario='.$idcalendario)
	 		->andWhere('fc.idtipo=7')
	 		->andWhere('fc.fin<=NOW()')
	    	->fetchOne();
	       
        return $q;
    }
}
