<?php

/**
 * LibrosActasTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class LibrosActasTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object LibrosActasTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('LibrosActas');
    }
    
    public static function obtenerLibros($idarea) {
    	$q = Doctrine_Query::create()
			->select('li.*')
			->from("LibrosActas li")
	    	->where("li.idarea = ".$idarea)
		->orderBy('li.descripcion ASC');

		return $q->execute();  	
    } 

    public static function obtenerLibroEquivalencia($idarea) {
    	$q = Doctrine_Query::create()
    	->select('li.*')
    	->from("LibrosActas li")
    	->where("li.idarea = ".$idarea)
    	->andWhere("li.descripcion like 'Equivalencia'")
    	->orderBy('li.descripcion ASC');
    
    	return $q->fetchOne();
    }

}
