  //********** Funcion Esquema Planilla de Asistencia*************
  public function EsquemaPlanillaAsistencia2daHoja($bucles)
  {
	// Agrega una nueva pagina
	$this->AddPage('P');
	// Asigna el ancho a la linea
	$this->SetLineWidth(1);
	// Linea horizontal que separa las Fechas y la cabecera del listado
	$this->Line(11,9.5,199,9.5);
	// Muestra la cabecera de la lista
	$this->SetFont('Times','',9);
	$this->SetXY(11,3);
	$this->Cell(6,9,"Nro.",0,1,'C');
	$this->SetXY(17,3);
	$this->Cell(54,9,"ALUMNOS",0,1,'C');
	$this->SetXY(195,3);
	$this->Cell(6,9,"Hs.",0,1,'C');
	$x=70;
	$cont = 1;
	$this->SetFont('Times','',9);
	while ($cont <= $bucles) {
		$this->SetXY($x,3);
		$this->Cell(25,9,"Hs.:",0,1,'C');
		$x = $x + 25;
		$cont++;
	}
  }
