class Provincias extends BaseProvincias
{
    public function __toString() {
        return $this->getDescripcion();
    }	
    
  // Obtiene todas las ciudades para dicha provincia 
  public function obtenerCiudades() 
    {
	    $q = Doctrine_Query::create()
	  		->select('*')
	 		->from('Ciudades c')
	    	->where('c.idprovincia = '.$this->getIdprovincia())
	    	->orderBy('c.descripcion ASC');
	    	
		return $q->execute();
    }	
}
