<?php

/**
 * SolicitudesLibredeudaTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class SolicitudesLibredeudaTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object SolicitudesLibredeudaTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('SolicitudesLibredeuda');
    }

    public function getSolicitudesPendientes()
	{
		$q = "SELECT s.* FROM solicitudes_libredeuda s WHERE s.idestadosolicitud =1 ORDER BY s.id ";
		$con = Doctrine_Manager::getInstance()->connection();
		$st = $con->execute($q);
		$rs = $st->fetchAll();
		return $rs;
	}    
}