class Profile extends PluginProfile
{
  // Busca el usuario de la persona
  public function obtenerUsuario()
    {
    	$q = Doctrine_Query::create()
	  		->select("*")
	 		->from("sfGuardUser u")
	 		->where("u.id = ".$this->getSfGuardUserId())
	    	->fetchOne();
	       
        return $q;
    }    
}
